import 'package:bloc/bloc.dart';import 'package:meta/meta.dart';import 'package:random_cats/data/source/hive_model/hive_model.dart';import '../../utils/hive_helper.dart';part 'basket_event.dart';part 'basket_state.dart';class BasketBloc extends Bloc<BasketEvent, BasketState> {  BasketBloc() : super(BasketInitial()) {    on<GetAllProductsFromBasketEvent>((event, emit) async {      final data = HiveHelper.getAllCats();      print(HiveHelper.getAllCats().length);      emit(GetAllProductsFromBasketState(data));    });    on<DeleteProductFromBasketEvent>((event, emit) async {      HiveHelper.deleteCats(event.product);      final data = HiveHelper.getAllCats();      emit(GetAllProductsFromBasketState(data));    });    on<DeleteBasket>((event, emit) async {      HiveHelper.deleteBasket();      final data = HiveHelper.getAllCats();      emit(GetAllProductsFromBasketState(data));    });  }}