import 'package:hive/hive.dart';import 'package:random_cats/data/source/model/cat_model.dart';import 'package:hive_flutter/adapters.dart';import '../data/source/hive_model/cat_adapter.dart';import '../data/source/hive_model/hive_model.dart';class HiveHelper {  static Future<void> init() async {    await Hive.initFlutter();    Hive.registerAdapter(HiveAdapter());    await Hive.openBox('basket');  }  static Future<void> addCatToBasket(HiveModel myModel) async {    var box = Hive.box('basket');    bool isBasket = true;    if (box.values.contains(myModel.text)) {      isBasket = false;    }    if (isBasket) {      box.add(myModel);    }  }  static Future<void> deleteCats(HiveModel myModel) async {    var box = Hive.box('basket');    int indexToDelete = -1;    for (int i = 0; i < box.length; i++) {      HiveModel productInBasket = box.getAt(i);      if (productInBasket.text == myModel.text) {        indexToDelete = i;        break;      }    }    if (indexToDelete != -1) {      await box.deleteAt(indexToDelete);    }  }  // static String getBasketTotalSum() {  //   final box = Hive.box('basket');  //   double totalSum = 0;  //   for (var product in box.values.cast<SpecialItemData>()) {  //     totalSum += (double.tryParse(product.value) ?? 0) *  //         (double.tryParse(product.price) ?? 0);  //   }  //   return totalSum.toString();  // }  // static Future<void> addProductToFavourite(SpecialItemData productHolder) async {  //   var box = Hive.box('favourite');  //   bool isFavourite = true;  //   if (box.values.contains(productHolder.name)) {  //     isFavourite = false;  //   }  //   if (isFavourite) {  //     box.add(productHolder);  //   }  // }  static List<HiveModel> getAllCats() {    final box = Hive.box('basket');    return box.values.cast<HiveModel>().toList();  }  static Future<void> deleteBasket() {    final box = Hive.box('basket');    return box.clear();  }}